package com.murray.com.murray.controller;import com.murray.com.murray.model.UserInfo;import com.murray.com.murray.service.UserInfoService;import org.apache.log4j.Logger;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RestController;/** * Created by chujulong on 2017/2/15. */@RestController@RequestMapping("/userinfo")public class UserInfoController {    private Logger logger = Logger.getLogger(UserInfoController.class);    @Autowired    private UserInfoService userInfoService;    @RequestMapping(value = "/getUserInfo",method = RequestMethod.GET)    public UserInfo getUserInfo() {        UserInfo userInfo=userInfoService.getUserInfo();        if(userInfo==null){            logger.info("UserInfo == NULL");        }        return userInfo;    }}