package com.murray.controller;import com.murray.model.UserInfo;import com.murray.service.UserInfoService;import org.apache.log4j.Logger;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.List;/** * Created by chujulong on 2017/2/15. */@RestController@RequestMapping("/userinfo")public class UserInfoController {    private Logger logger = Logger.getLogger(UserInfoController.class);    @Autowired    private UserInfoService userInfoService;    @RequestMapping(value = "", method = RequestMethod.GET)    public List<UserInfo> getUserInfo() {        List<UserInfo> userInfo = userInfoService.getUserInfo();        if (userInfo == null) {            logger.info("UserInfo == NULL");        }        return userInfo;    }    @RequestMapping(value = "/{id}", method = RequestMethod.GET)    public UserInfo getById(@PathVariable int id) {        UserInfo userInfo = userInfoService.getById(id);        if (userInfo == null) {            logger.info("UserInfo == NULL");        }        return userInfo;    }    @RequestMapping(value = "/{id}", method = RequestMethod.POST)    public int removeById(@PathVariable int id) {        int result = userInfoService.removeById(id);        return result;    }    @RequestMapping(value = "", method = RequestMethod.PUT)    public int modifyUserInfo(@RequestBody UserInfo userInfo) {        int result = userInfoService.modifyUserInfo(userInfo);        return result;    }    @RequestMapping(value = "", method = RequestMethod.POST)    public int createUserInfo(@RequestBody UserInfo userInfo) {        int result = userInfoService.createUserInfo(userInfo);        return result;    }}