package com.murray.service;import com.murray.mapper.UserInfoMapper;import com.murray.model.UserInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * Created by chujulong on 2017/2/15. */@Servicepublic class UserInfoService {    @Autowired    private UserInfoMapper userInfoMapper;    public List<UserInfo> getUserInfo() {        return userInfoMapper.selectAll();    }    public UserInfo getById(int id) {        return userInfoMapper.selectById(id);    }    public int removeById(int id) {        return userInfoMapper.delete(id);    }    public int modifyUserInfo(UserInfo userInfo) {        return userInfoMapper.update(userInfo);    }    public int createUserInfo(UserInfo userInfo) {        return userInfoMapper.insert(userInfo);    }}